import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Navbar from 'react-bootstrap/Navbar';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import Placeholder from 'react-bootstrap/Placeholder';

import "../CSS/sidebar.css";
import useFetch from '../Hook/useFetch';

function Sidebar() {

  const [location, setLocation] = useState('');
  const { loading, error, data, doFetch } = useFetch();

  const handleSubmit = async (event) => {
    event.preventDefault();
    doFetch(`http://localhost:1337/api/weather?location=${location}`);
  };

  useEffect(() => {
    if (data) {
      console.log(data);
      console.log(data.condition_text);
    }
  }, [data]);

  useEffect(() => {
    if (error) {
      console.log(error);
    }
  }, [error]);



  if (loading) return (
    <Navbar expand="lg" className="bg-body-tertiary flex-column side-bar">
      <Container fluid className='d-flex flex-column'>
        <Placeholder as={Navbar.Brand} animation="glow">
          <Placeholder xs={6} />
        </Placeholder>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll" className="flex-column">
          <Form>
            <Placeholder as={Form.Control} xs={6} animation="glow" />
            <Placeholder as={Button} variant="outline-dark" xs={6} animation="glow" />
          </Form>
        </Navbar.Collapse>
        <Card className='mt-5' border="secondary" bg='dark' text='light' style={{ width: '15rem' }}>
          <Card.Body>
            <Placeholder as={Card.Title} animation="glow">
              <Placeholder xs={6} />
            </Placeholder>
          </Card.Body>
          <ListGroup className="list-group-flush" bg='dark' text='light'>
            <Placeholder as={ListGroup.Item} animation="glow">
              <Placeholder xs={6} />
            </Placeholder>
            <Placeholder as={ListGroup.Item} animation="glow">
              <Placeholder xs={6} />
            </Placeholder>
            <Placeholder as={ListGroup.Item} animation="glow">
              <Placeholder xs={6} />
            </Placeholder>
            <Placeholder as={ListGroup.Item} animation="glow">
              <Placeholder xs={6} />
            </Placeholder>
          </ListGroup>
          <Placeholder animation="glow" className='p-5' variant="bottom" style={{ height: '300px' }}>
            <Placeholder xs={6} />
          </Placeholder>
        </Card>
      </Container>
    </Navbar>
  );


  if (error) return <p>Error :|</p>


  return (
    <div>
      <Navbar expand="lg" className="bg-body-tertiary flex-column side-bar">
        <Container fluid className='d-flex flex-column'>
          <Navbar.Brand className='d-flex mb-2' href="#"><i className="bi bi-geo-alt me-2 fs-1"></i><p className='fs-1'>Location</p></Navbar.Brand>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll" className="flex-column">

            <Form className="d-flex flex-column" style={{ width: '15rem' }} onSubmit={handleSubmit}>
              <Form.Control
                type="search"
                placeholder="eg. Delhi"
                className="me-2 mb-2"
                aria-label="Search"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
              />
              <Button variant="outline-dark" type='submit' disabled={loading}>Search</Button>
            </Form>
          </Navbar.Collapse>
          {data && data.data ? (
            data.data.map(weather => (
              weather.attributes && (
                <div>
                  <Card className='mt-5' border="secondary" bg='dark' text='light' style={{ width: '15rem' }}>
                    <Card.Body>
                      <Card.Title>{weather.location_name}</Card.Title>
                    </Card.Body>
                    <ListGroup className="list-group-flush" bg='dark' text='light' key={weather.id}>
                      <ListGroup.Item action variant="light">{weather.location_name}</ListGroup.Item>
                      <ListGroup.Item action variant="light">{weather.location_region}</ListGroup.Item>
                      <ListGroup.Item action variant="light">{weather.condition_text}</ListGroup.Item>
                    </ListGroup>
                    <Card.Img className='p-5' variant="bottom" src={weather.condition_icon} />
                  </Card>
                </div>
              )
            ))
          ) : (
            <p>No data available</p>
          )}



        </Container>
      </Navbar>
    </div>
  )
}

export default Sidebar;
