import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Stack from 'react-bootstrap/Stack';
import ProgressBar from 'react-bootstrap/ProgressBar';
import '../CSS/weatherpanel.css'
import { useContext } from 'react';
import { WeatherContext } from '../WeatherContext';


function Weatherpanel() {

    const { loading, error, data } = useContext(WeatherContext);
    if (loading) return <p>Loading....</p>
    if (error) return <p>Error :|</p>
    if (!data) return <p>No data Available</p>

    return (
        <Container className='vh-100'>
            <Row>
                <Col md={4}><p className='fs-3 m-5 weather-info-location'>{data.location_name}</p></Col>
                <Col md={{ span: 4, offset: 4 }}><p className='fs-3 m-5 pl-2 weather-info-location'>{data.forecast_date}</p></Col>
            </Row>
            <Row>
                <Col xs={9}>
                    <div className="temperature">{data.current_temp_c}Â°c</div>
                </Col>
                <Col className='weather-info'>
                    <Stack direction="vertical pl-2">
                        <div className='pb-1'>{data.wind_mph}</div>
                        <div className='pb-1'>{data.humidity}</div>
                        <div className='pb-1'>{data.feelslike_c}</div>
                        <div className='pb-1'>{data.air_quality_pm10}</div>
                    </Stack>
                </Col>
            </Row>
            <Row>

            </Row>
        </Container>
    )
}

export default Weatherpanel;
